mapper.select:
select * from (select m.member_no, m.member_introduction, m.member_nickname, m.member_image, max(mm.mileage_accumulated) score, row_number() over(order by m.member_no desc) rnum from member m left join mileage_master mm on m.member_no = mm.mileage_memno where mileage_accumulated is not null and mileage_type != '회원가입 적립'
and m.member_nickname like '%'||#{keyword}||'%'
group by member_no, member_introduction, member_nickname, member_image order by score desc) where rnum >= 1 and rnum <= #{rnum}

select * from (select m.member_no, m.member_introduction, m.member_nickname, m.member_image, count(mm.mileage_type) score, row_number() over(order by m.member_no desc) rnum from member m left join mileage_master mm on m.member_no=mm.mileage_memno where mm.mileage_type='게시글 등록 획득'
and m.member_nickname like '%'||#{keyword}||'%'
group by member_no, member_introduction, member_nickname, member_image order by score desc) where rnum >= 1 and rnum <= #{rnum}

select * from (select m.member_no, m.member_introduction, m.member_nickname, m.member_image, count(mm.mileage_type) score, row_number() over(order by m.member_no desc) rnum from member m left join mileage_master mm on m.member_no=mm.mileage_memno where mm.mileage_type='제품구매 획득'
and m.member_nickname like '%'||#{keyword}||'%'
group by member_no, member_introduction, member_nickname, member_image order by score desc) where rnum >= 1 and rnum <= #{rnum}

select * from (select m.member_id, m.member_introduction, m.member_nickname, m.member_image, max(cb.community_recommend) score, row_number() over(order by m.member_id desc) rnum from member m left join community_board cb on m.member_id=cb.community_userid where community_recommend is not null
and m.member_nickname like '%'||#{keyword}||'%'
group by member_id, member_introduction, member_nickname, member_image order by score desc) where rnum >= 1 and rnum <= #{rnum}
